package com.springJpa.demoSpringDataJpa.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.math.BigDecimal;import java.time.LocalDateTime;@NoArgsConstructor@AllArgsConstructor@Getter@Setter@Entity@Table( name = "products" ,        schema = "ecommerce" ,        uniqueConstraints = {          @UniqueConstraint( name = "sku_unique", columnNames = "stock_keeping_unit")        }      )public class Product {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private String id;    @Column( name = "stock_keeping_unit" , nullable = false)    private String sku;    @Column( nullable = false)    private String name;    private String description;    private String BigDecimal;    private String imageUrl;     private boolean active;     private BigDecimal price;     private LocalDateTime dateCreated;     private LocalDateTime lastUpdated ;}